import React, { useState, useEffect } from "react";
import DataTable from "react-data-table-component";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import { loadData, uploadFile, updateRecord } from "./service";

function App() {
  const [users, setUsers] = useState({ data: [], count: 0 });
  const [givenValue, setGivenValue] = useState({});
  const [page, setPage] = useState(1);
  const [show, setShow] = useState(false);
  const [editShow, setEditShow] = useState(false);
  const [file, setFile] = useState();
  const countPerPage = 10;

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const handleEditClose = () => setEditShow(false);
  const handleEditShow = () => setEditShow(true);

  const getUserList = async () => {
    const data = await loadData(page, countPerPage);
    setUsers(data);
  };

  useEffect(() => {
    getUserList();
  }, [page]);

  const handleEdit = (row) => {
    handleEditShow();
    setGivenValue(row);
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    if (file) {
      const data = await uploadFile(file);
      alert("File uploaded successfully");
      setPage(1);
      getUserList();
      handleClose();
      setFile(null); // Clear the previous file data
    }
  };

  const handleEdtFormSubmit = async (e) => {
    e.preventDefault();
    const data = await updateRecord(givenValue);
    alert("Record updated successfully");
    getUserList();
    handleEditClose();
  };

  const handleFileUpload = (event) => {
    setFile(event.target.files[0]);
  };

  const columns = [
    {
      name: "Sno",
      selector: "id",
    },
    {
      name: "SKU",
      selector: "SKU",
    },
    {
      name: "ProductName",
      selector: "ProductName",
    },
    {
      name: "Price",
      selector: "Price",
    },
    {
      name: "StoreID",
      selector: "StoreID",
    },
    {
      name: "Action",
      cell: (row) => (
        <Button variant="primary" onClick={() => handleEdit(row)}>
          Edit
        </Button>
      ),
    },
  ];

  return (
    <div className="App">
      <Button variant="primary float-right float-end" onClick={handleShow}>
        Upload CSV
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Please Upload Store Product File as CSV</Modal.Title>
        </Modal.Header>
        <form onSubmit={handleFormSubmit}>
          <Modal.Body>
            <input type="file" onChange={handleFileUpload} name="uploadcsv" />
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" type="button" onClick={handleClose}>
              Close
            </Button>
            <Button variant="primary" type="submit">
              Save Changes
            </Button>
          </Modal.Footer>
        </form>
      </Modal>

      <Modal show={editShow} onHide={handleEditClose}>
        <Modal.Header closeButton>
          <Modal.Title>Please Edit Price Details</Modal.Title>
        </Modal.Header>
        <form onSubmit={handleEdtFormSubmit}>
          <Modal.Body>
            <div className="row">
              <div className="col-md-5">
                <h5>SKU</h5>
              </div>
              <div className="col-md-7">
                <input
                  type="text"
                  className="form-control"
                  defaultValue={givenValue?.SKU}
                  name="sku"
                  placeholder="SKU"
                  onChange={(e) =>
                    setGivenValue({
                      ...givenValue,
                      SKU: e.target.value,
                    })
                  }
                />
              </div>
            </div>

            <div className="row">
              <div className="col-md-5">
                <h5>Product Name</h5>
              </div>
              <div className="col-md-7">
                <input
                  type="text"
                  className="form-control"
                  defaultValue={givenValue?.ProductName}
                  name="ProductName"
                  placeholder="ProductName"
                  onChange={(e) =>
                    setGivenValue({
                      ...givenValue,
                      ProductName: e.target.value,
                    })
                  }
                />
              </div>
            </div>

            <div className="row">
              <div className="col-md-5">
                <h5>Price</h5>
              </div>
              <div className="col-md-7">
                <input
                  type="text"
                  className="form-control"
                  defaultValue={givenValue?.Price}
                  name="Price"
                  placeholder="Price"
                  onChange={(e) =>
                    setGivenValue({
                      ...givenValue,
                      Price: e.target.value,
                    })
                  }
                />
              </div>
            </div>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" type="button" onClick={handleEditClose}>
              Close
            </Button>
            <Button variant="primary" type="submit">
              Save Changes
            </Button>
          </Modal.Footer>
        </form>
      </Modal>

      {users.count > 0 ? (
        <DataTable
          title="Products"
          columns={columns}
          data={users.data}
          highlightOnHover
          pagination
          paginationServer
          paginationTotalRows={users.count}
          paginationRowsPerPageOptions={[5, 15, 25, 50]}
          paginationPerPage={countPerPage}
          paginationComponentOptions={{
            noRowsPerPage: true,
          }}
          onChangePage={setPage}
        />
      ) : (
        <div className="text-center mt-4">
          <h4>No data available</h4>
        </div>
      )}
    </div>
  );
}

export default App;
